INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-1', STRINGDECODE('\uc124\uc545\uc0b0 \ud0d0\ubc29\uc548\ub0b4\uc18c, \uc2e0\ud765\uc0ac, \uad8c\uae08\uc131, \ube44\ub8e1\ud3ed\ud3ec'), STRINGDECODE(' \uc124\uc545\uc0b0\uc740 \uc65c \uc124\uc545\uc0b0\uc774\uace0, \uc2e0\ud765\uc0ac\ub294 \uc65c \uc2e0\ud765\uc0ac\uc77c\uae4c\uc694? \uc124\uc545\uc0b0\uc5d0 \ub300\ud574 \uc815\ud655\ud788 \uc54c\uace0, \ubc30\uc6b0\uace0, \ub290\ub084 \uc218 \uc788\ub294 \ub2f9\uc77c\ud615 \uc0dd\ud0dc\uad00\uad11\uc785\ub2c8\ub2e4.'), STRINGDECODE('\uc790\uc5f0\uacfc \ubb38\ud654\ub97c \ud568\uaed8 \uc990\uae30\ub294 \uc124\uc545\uc0b0 \uae30\ud589')),
('prg-2', STRINGDECODE('\uc18c\uaddc\ubaa8 \ud559\uc0dd\uc744 \uc704\ud55c \uc218\ud559\uc5ec\ud589 \ud504\ub85c\uadf8\ub7a8!'), STRINGDECODE(' \ubcf4\ub294 \uc218\ud559\uc5ec\ud589\uc774 \uc544\ub2cc \ud558\ub294 \uc218\ud559\uc5ec\ud589!\n \ud574\uc124\uc774 \uc788\uace0, \uc990\uac70\uc6b4 \uc0b0\uc73c\ub85c\uc758 \uc5ec\ud589!\n \ub9c8\uc744\uc5d0\uc11c \ub180\uba74\uc11c \ubc30\uc6b0\ub294 \uc5ec\ud589!'), STRINGDECODE('[\uc124\uc545\uc0b0] \uc124\uc545\uc0b0\uc5d0\uc11c \uae38\uc744 \ubb3b\ub2e4(\uc124\uc545\uc744 \ub0b4 \ud488\uc5d0)')),
('prg-3', STRINGDECODE('\uc6d4\uc815\uc0ac, \uc804\ub098\ubb34 \uc232\uae38, \uc131\ubcf4\ubc15\ubb3c\uad00'), STRINGDECODE(' \u2605 \uc6d4\uc815\uc0ac \uc5ed\uc0ac\u00b7\ubb38\ud654 \ud574\uc124\n \u2605 \uc804\ub098\ubb34 \uc232 \uc790\uc5f0\ud574\uc124\n \u2605 \uc131\ubcf4\ubc15\ubb3c\uad00 \ud0d0\ubc29'), STRINGDECODE('\uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ud574\ud53c700!! \ubb38\ud654\u00b7\uc0dd\ud0dc \uc5ec\ud589')),
('prg-4', STRINGDECODE('\uc232\uce58\uc720, \ud658\uacbd\uc131\uc9c8\ud658 \uad50\uc721 \ubc0f \uc0c1\ub2f4, \uce5c\ud658\uacbd \ub9cc\ub4e4\uae30\uccb4\ud5d8 \ub4f1'), STRINGDECODE(' -\uc232\uce58\uc720\ud65c\ub3d9 : \uae08\uac15\uc18c\ub098\ubb34\uc232\uae38\uacfc \uc804\ub098\ubb34\uc232\uc5d0\uc11c\uc758 \ub180\uc774\uc640 \uba85\uc0c1, \uccb4\uc870\ub97c \ud1b5\ud55c \uce58\uc720\n -\ud658\uacbd\uc131\uc9c8\ud658 \uad50\uc721 : \uc804\ubb38 \uc758\ub8cc\uc778\uc758 \uad50\uc721 \ubc0f \uc0c1\ub2f4/\uc9c4\ub2e8\uc744 \ud1b5\ud55c \ud658\uacbd\uc131\uc9c8\ud658 \uc608\ubc29 \ubc0f \uc870\uae30\uce58\uc720 \ubc29\ubc95 \uc804\ub2ec\n -\uce5c\ud658\uacbd \ub9cc\ub4e4\uae30 : \ubc24\ud638\ubc15\ucc10\ubc29, \ud669\ud1a0\ud5c8\ube0c\ube44\ub204 \ub4f1 \ubab8\uc5d0 \uc88b\uc740 \uba39\uac70\ub9ac\uc640 \ube44\ub204\ub97c \uc9c1\uc811 \ub9cc\ub4dc\ub294 \uccb4\ud5d8'), STRINGDECODE('\uce58\uc545\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uacfc \ud568\uaed8\ud558\ub294 \uac74\uac15\ub098\ub204\ub9ac\ucea0\ud504')),
('prg-5', STRINGDECODE('\uad6c\ub8e1\uc9c0\uad6c \uba85\ud488 \uae08\uac15\uc18c\ub098\ubb34\uae38 \uccb4\ud5d8, \ud559\uace11\ub9ac\ud5a5\ud1a0\uc694\ub9ac\uccb4\ud5d8\uc7a5'), STRINGDECODE(' \uad6d\ub9bd\uacf5\uc6d0\uc758 \uc2e0\ube44\ud55c \uc790\uc5f0 \uc0dd\ud0dc \uccb4\ud5d8\uacfc  \ub18d\ucd0c\ub9c8\uc744\uc5d0\uc11c\uc758 \uc804\ud1b5\uc74c\uc2dd, \uc804\ud1b5\ub180\uc774 \uccb4\ud5d8\uc744 \ud1b5\ud558\uc5ec \uc790\uc5f0\uacfc \uad50\uac10\ud558\uace0 \uc78a\ud600\uc838\uac00\ub294 \uc6b0\ub9ac \ubb38\ud654\uc758 \uc18c\uc911\ud568\uc744 \ub418\uc0c8\uae38 \uc218 \uc788\ub294 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.  '), STRINGDECODE('FTA\uc640 \ud568\uaed8 \ud558\ub294 \ub179\uc0c9\uacf5\uac04 \uc18d \uac74\uac15\ucc3e\uae30(\ub2f9\uc77c\ud615)')),
('prg-6', STRINGDECODE('\uba85\ud488 \uae08\uac15\uc18c\ub098\ubb34\uae38 \uccb4\ud5d8(\uc790\uc5f0\ud574\uc124, \uc232\uba85\uc0c1, \uc232\uce58\uc720, \ub9e8\ubc1c\uac77\uae30 \ub4f1), \uc544\ube60\uc640 \ucea0\ud504 \ub4f1'), STRINGDECODE(' \uc5ec\ub984 \ud734\uac00\ucca0\uc744 \ub9de\uc544 \ud658\uacbd\uc744 \uc0ac\ub791\ud558\ub294 \uac00\uc871\uc744 \ub300\uc0c1\uc73c\ub85c \uc6b4\uc601\ud558\ub294 \uce5c\ud658\uacbd \ucea0\ud551 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4. \uc232\uacfc \uacc4\uace1\uc5d0 \uc0b4\uace0 \uc788\ub294 \uc790\uc5f0\uc758 \uce5c\uad6c\ub4e4\ub3c4 \ub9cc\ub098\ubcf4\uace0 \ub180\uc774\ub97c \ud1b5\ud574 \uc9c0\uad6c \ud658\uacbd\uc758 \uc911\uc694\uc131\ub3c4 \ub290\uaef4\ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c \uc7ac\ubbf8\uc788\ub294 \uac00\uc871\ub300\ud56d \uac8c\uc784\uc744 \ud1b5\ud558\uc5ec \uac00\uc871\uc758 \uc815\uc744 \ubd81\ub3cb\uc744\uc218 \uc788\ub294 \ucd94\uc5b5\uc758 \uc2dc\uac04\uc744 \ub9c8\ub828\ud574 \ub4dc\ub9bd\ub2c8\ub2e4.'), STRINGDECODE('\uc544\ube60\uc640 \ud568\uaed8 \ud558\ub294 \uc800\ud0c4\uc18c \ub179\uc0c9\ud734\uac00(1\ubc152\uc77c)'));
INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-7', STRINGDECODE('\uad6c\ub8e1\uc9c0\uad6c \uae08\uac15\uc18c\ub098\ubb34 \uba85\ud488\ub179\uc0c9\uae38 \uccb4\ud5d8, \ud559\uace11\ub9ac\ud5a5\ud1a0\uc694\ub9ac\uccb4\ud5d8\uc7a5'), STRINGDECODE(' \uae30\uc874 \uc6b4\uc601\ub418\ub358 \uad6d\ub9bd\uacf5\uc6d0 \uc790\uc5f0\ud574\uc124\uc5d0 \uacf5\uc6d0 \uc778\uadfc\uc9c0\uc5ed\uc758 \ud2b9\uc131\uc744 \ubc18\uc601\ud55c \ub18d\ucd0c\uccb4\ud5d8\uc744 \uac00\ubbf8\ud568\uc73c\ub85c\uc368 \ub2e4\uc591\ud55c \uccb4\ud5d8\uac70\ub9ac\ub97c \uc81c\uacf5\ud558\uace0 \uc9c0\uc5ed\uacbd\uc81c \ud65c\uc131\ud654 \ubfd0\ub9cc \uc544\ub2c8\ub77c \uc9c0\uc5ed\uc8fc\ubbfc\uacfc\uc758 \uc720\ub300\uad00\uacc4 \ud615\uc131\uc5d0\ub3c4 \ud06c\uac8c \uae30\uc5ec\ud558\ub294 \uace0\ud488\uaca9 \uc0dd\ud0dc\uad00\uad11'), STRINGDECODE('FTA\uc640 \ud568\uaed8 \ud558\ub294 \ub179\uc0c9\uacf5\uac04\uc18d \uac74\uac15\ucc3e\uae30(\ub2f9\uc77c\ud615)')),
('prg-8', STRINGDECODE('\uac15\uc6d0\uac10\uc601, \uad6d\ub9bd\uacf5\uc6d0 \uc5d0\ucf54\ud22c\uc5b4 \ud574\uc124, \uba78\uc885\uc704\uae30\ub3d9\uc2dd\ubb3c\uc6d0, \ub18d\ucd0c\uccb4\ud5d8, \ubc15\uacbd\ub9ac \ubb38\ud559\uacf5\uc6d0, \ud15c\ud50c\uc2a4\ud14c\uc774 \uccb4\ud5d8, \ub808\ud06c\ub808\uc774\uc158, \uace0\ud310\ud654 \ubc15\ubb3c\uad00'), STRINGDECODE(' \uc218\ub824\ud55c \uacbd\uad00\uc758 \uce58\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uc740 \uac15\uc6d0\uad8c\uc758 \uad50\ud1b5\uc694\uc9c0\uc778 \uc6d0\uc8fc\uc2dc\uc5d0 \uc778\uc811\ud574 \uc788\uc5b4 \uc218\ub3c4\uad8c\uacfc\uc758 \uac70\ub9ac\uac00 \uac00\uae5d\uc2b5\ub2c8\ub2e4.\n \uc790\uc5f0\uc744 \uc81c\ub300\ub85c \ub290\ub084 \uc218 \uc788\ub294 \uad6d\ub9bd\uacf5\uc6d0 \uc5d0\ucf54\ud22c\uc5b4\uc758 \ub2e4\uc591\ud55c \ud504\ub85c\uadf8\ub7a8\uc744 \ud1b5\ud574 \uc0c9\ub2e4\ub978 \uc218\ud559\uc5ec\ud589\uc744 \uc990\uae38 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \ubb38\ud654\uac00 \uc788\ub294 \uc218\ud559\uc5ec\ud589\uc73c\ub85c\uc11c \ubc15\uacbd\ub9ac \ubb38\ud559\uad00\uacfc \uace0\ud310\ud654 \ubc15\ubb3c\uad00\uc744 \ud1b5\ud574 \ub2e4\uc591\ud55c \uc804\uc2dc\ubb3c \uad00\ub78c\uacfc \ud310\ud654 \uccb4\ud5d8 \ub4f1\uc744 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \uc544\ub984\ub2e4\uc6b4 \uc790\uc5f0\uacfc \ud568\uaed8 \ud558\uace0 \uc2f6\ub2e4\uba74 \uc57c\uc601\uc744, \uccb4\ud5d8\ud615 \uc219\ubc15\uc744 \uc6d0\ud55c\ub2e4\uba74 \ud15c\ud50c\uc2a4\ud14c\uc774\ub97c, \uc9c0\uc5ed\uc8fc\ubbfc\uc758 \ub530\ub73b\ud55c \ub9c8\uc74c\uc744 \ub290\ub07c\uace0 \uc2f6\ub2e4\uba74 \ubbfc\ubc15\uc744 \uc120\ud0dd \ud560 \uc218 \uc788\ub294 \uc219\ubc15\uc758 \ub2e4\uc591\ud654\uac00 \uad6c\ucd95\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4.\n \uc790\uc5f0\uc758 \ud5a5\uae30\ub97c \uc81c\ub300\ub85c \ub290\ub07c\uace0 \uc0c9\ub2e4\ub978 \uccb4\ud5d8\uc744 \uc6d0\ud55c\ub2e4\uba74 ''\uc790\uc5f0\uacfc \ubb38\ud654\uac00 \uc788\ub294 \uce58\uc545\uc0b0 \uc218\ud559\uc5ec\ud589''\uc5d0 \ucc38\uc5ec\ud574 \ubcf4\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4.'), STRINGDECODE('\uc790\uc5f0\uacfc \ubb38\ud654\uac00 \uc788\ub294 \uce58\uc545\uc0b0 \uc218\ud559\uc5ec\ud589')),
('prg-9', STRINGDECODE('\uae08\uac15\uc18c\ub098\ubb34 \uba85\ud488\ub179\uc0c9\uae38 \uccb4\ud5d8, \ub18d\ucd0c \uccb4\ud5d8, \uc0ac\ucc30\ubb38\ud654 \uccb4\ud5d8, \uad70\ubd80\ub300 \uccb4\ud5d8 '), STRINGDECODE(' \uad6d\ub9bd\uacf5\uc6d0 \uc0dd\ud0dc\uccb4\ud5d8\uacfc \ub354\ubd88\uc5b4 Farm stay(\ub18d\ucd0c\uccb4\ud5d8), Temple stay(\uc0ac\ucc30\uccb4\ud5d8) \ubc0f Army stay(\ubcd1\uc601\uccb4\ud5d8) \ub4f1 \ub2e4\uc591\ud55c \ubd84\uc57c\uc758 \uccb4\ud5d8\uc744 \ud1b5\ud558\uc5ec \uc790\uc5f0\uacfc \uc6b0\ub9ac \ubb38\ud654\uc758 \uc18c\uc911\ud568\uc744 \ub290\ub07c\uace0 \uc0b6\uc758 \ud65c\ub825\uc18c\ub97c \ub418\ucc3e\ub294 \uce58\uc545\uc0b0 \ub300\ud45c \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4. '), STRINGDECODE('FTA\uc640 \ud568\uaed8 \ud558\ub294 \ub179\uc0c9\uacf5\uac04 \uc18d \uac74\uac15\ucc3e\uae30(1\ubc152\uc77c)')),
('prg-10', STRINGDECODE('\uae08\uac15\uc18c\ub098\ubb34 \uba85\ud488\ub179\uc0c9\uae38 \uccb4\ud5d8, \ub18d\ucd0c \uccb4\ud5d8, \uc0ac\ucc30\ubb38\ud654 \uccb4\ud5d8, \uad70\ubd80\ub300 \uccb4\ud5d8'), STRINGDECODE(' \uad6d\ub9bd\uacf5\uc6d0\uc758 \uc790\uc5f0\ud574\uc124\uc5d0 F(\ud31c\uc2a4\ud14c\uc774), T(\ud15c\ud50c\uc2a4\ud14c\uc774), A(\uc544\ubbf8\uc2a4\ud14c\uc774) \uccb4\ud5d8\uc744 \uac00\ubbf8\ud568\uc73c\ub85c\uc368 \uc218\ud3c9\ud615 \ud0d0\ubc29\ubb38\ud654 \uc720\ub3c4\ub97c \ud1b5\ud55c \uad6d\ub9bd\uacf5\uc6d0 \ud6fc\uc190\uc744 \ucd5c\uc18c\ud654 \ud558\uace0 \uc9c0\uc5ed\uacbd\uc81c \ud65c\uc131\ud654\uc5d0\ub3c4 \ub3c4\uc6c0\uc744 \uc904\uc218\uc788\ub294 \uace0\ud488\uaca9 \uc0dd\ud0dc\uad00\uad11'), STRINGDECODE('FTA\uc640 \ud568\uaed8 \ud558\ub294 \ub179\uc0c9\uacf5\uac04\uc18d \uac74\uac15\ucc3e\uae30(1\ubc152\uc77c)'));
INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-11', STRINGDECODE('\uac15\uc6d0\uac10\uc601, \uad6d\ub9bd\uacf5\uc6d0 \uc5d0\ucf54\ud22c\uc5b4 \ud574\uc124, \uba78\uc885\uc704\uae30\ub3d9\uc2dd\ubb3c\uc6d0, \ub18d\ucd0c\uccb4\ud5d8, \ubc15\uacbd\ub9ac \ubb38\ud559\uacf5\uc6d0, \ud15c\ud50c\uc2a4\ud14c\uc774 \uccb4\ud5d8, \ub808\ud06c\ub808\uc774\uc158, \uace0\ud310\ud654 \ubc15\ubb3c\uad00 '), STRINGDECODE(' \uc218\ub824\ud55c \uacbd\uad00\uc758 \uce58\uc545\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\uc740 \uac15\uc6d0\uad8c\uc758 \uad50\ud1b5\uc694\uc9c0\uc778 \uc6d0\uc8fc\uc2dc\uc5d0 \uc778\uc811\ud574 \uc788\uc5b4 \uc218\ub3c4\uad8c\uacfc\uc758 \uac70\ub9ac\uac00 \uac00\uae5d\uc2b5\ub2c8\ub2e4. \uc790\uc5f0\uc744 \uc81c\ub300\ub85c \ub290\ub084 \uc218 \uc788\ub294 \uad6d\ub9bd\uacf5\uc6d0 \uc5d0\ucf54\ud22c\uc5b4\uc758 \ub2e4\uc591\ud55c \ud504\ub85c\uadf8\ub7a8\uc744 \ud1b5\ud574 \uc0c9\ub2e4\ub978 \uc218\ud559\uc5ec\ud589\uc744 \uc990\uae38 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ubb38\ud654\uac00 \uc788\ub294 \uc218\ud559\uc5ec\ud589\uc73c\ub85c\uc11c \ubc15\uacbd\ub9ac \ubb38\ud559\uad00\uacfc \uace0\ud310\ud654 \ubc15\ubb3c\uad00\uc744 \ud1b5\ud574 \ub2e4\uc591\ud55c \uc804\uc2dc\ubb3c \uad00\ub78c\uacfc \ud310\ud654 \uccb4\ud5d8 \ub4f1\uc744 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc544\ub984\ub2e4\uc6b4 \uc790\uc5f0\uacfc \ud568\uaed8 \ud558\uace0 \uc2f6\ub2e4\uba74 \uc57c\uc601\uc744, \uccb4\ud5d8\ud615 \uc219\ubc15\uc744 \uc6d0\ud55c\ub2e4\uba74 \ud15c\ud50c\uc2a4\ud14c\uc774\ub97c, \uc9c0\uc5ed\uc8fc\ubbfc\uc758 \ub530\ub73b\ud55c \ub9c8\uc74c\uc744 \ub290\ub07c\uace0 \uc2f6\ub2e4\uba74 \ubbfc\ubc15\uc744 \uc120\ud0dd \ud560 \uc218 \uc788\ub294 \uc219\ubc15\uc758 \ub2e4\uc591\ud654\uac00 \uad6c\ucd95\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \uc790\uc5f0\uc758 \ud5a5\uae30\ub97c \uc81c\ub300\ub85c \ub290\ub07c\uace0 \uc0c9\ub2e4\ub978 \uccb4\ud5d8\uc744 \uc6d0\ud55c\ub2e4\uba74 ''\uc790\uc5f0\uacfc \ubb38\ud654\uac00 \uc788\ub294 \uce58\uc545\uc0b0 \uc218\ud559\uc5ec\ud589''\uc5d0 \ucc38\uc5ec\ud574 \ubcf4\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4. '), STRINGDECODE('\uc790\uc5f0\uacfc \ubb38\ud654\uac00 \uc788\ub294 \uce58\uc545\uc0b0 \uc218\ud559\uc5ec\ud589')),
('prg-12', STRINGDECODE('1\uc77c\ucc28: \uc5b4\ub984\uce58\ub9c8\uc744 \uc778\uadfc \ud0d0\ubc29, 2\uc77c\ucc28: \uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ud0d0\ubc29, 3\uc77c\ucc28: \ubd09\ud3c9\ub9c8\uc744 \ud0d0\ubc29'), STRINGDECODE('1\uc77c\ucc28: \ubc31\ub8e1\ub3d9\uad74, \ubbfc\ubb3c\uace0\uae30\uc0dd\ud0dc\uad00 \uccb4\ud5d8, \uce60\uc871\ub839 \ud2b8\ub808\ud0b9\n 2\uc77c\ucc28: \ub300\uad00\ub839 \uc591\ub5bc\ubaa9\uc7a5, \uc2e0\uc7ac\uc0dd \uc5d0\ub108\uc9c0\uc804\uc2dc\uad00, \uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0\n 3\uc77c\ucc28: \uc774\ud6a8\uc11d \ubb38\ud559\uad00, \ubd09\ud3c9\ub9c8\uc744'), STRINGDECODE('\uc624\uac10\ub9cc\uc871! \uc624\ub300\uc0b0 \uc5d0\ucf54 \uc5b4\ub4dc\ubca4\ucc98 \ud22c\uc5b4')),
('prg-13', STRINGDECODE('\uc120\uc7ac\uae38, \ud55c\uad6d\uc790\uc0dd\uc2dd\ubb3c\uc6d0, \uc804\ub098\ubb34\uc232, \uc6d4\uc815\uc0ac, \ubc29\uc544\ub2e4\ub9ac\uc57d\uc218'), STRINGDECODE(' \ucc9c\ub144\uc758 \uc232\uc73c\ub85c \ubd88\ub9ac\ub294 \uc624\ub300\uc0b0 \uc804\ub098\ubb34\uc232\uacfc \uc120\uc7ac\uae38\uc5d0\uc11c \ub2e4\uc591\ud55c \uc232\uce58\uc720 \ud504\ub85c\uadf8\ub7a8 \uccb4\ud5d8'), STRINGDECODE('\uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \ud790\ub9c1\ucea0\ud504')),
('prg-14', STRINGDECODE('\uc18c\uae08\uac15, \uc0bc\uc0b0\ud14c\ub9c8\ud30c\ud06c'), STRINGDECODE(' \uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc758 \ub300\ud45c \uacbd\uad00\uc778 \uc18c\uae08\uac15\uc9c0\uad6c\uc5d0\uc11c \ub300\uc790\uc5f0\uc758 \uc544\ub984\ub2e4\uc6c0\uc744 \uc81c\ub300\ub85c \uc990\uae34\ub2e4.'), STRINGDECODE('\uc18c\uae08\uac15 \uc9c0\uc5ed\ubb38\ud654 \uccb4\ud5d8')),
('prg-15', STRINGDECODE('\uc624\ub300\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uc804\ub098\ubb34\uc232, \uc6d4\uc815\uc0ac, \uc0c1\uc6d0\uc0ac, \uc2e0\uc7ac\uc0dd\uc5d0\ub108\uc9c0\uc804\uc2dc\uad00, \ub300\uad00\ub839 \uc591\ub5bc\ubaa9\uc7a5, \ud55c\uad6d\uc790\uc0dd\uc2dd\ubb3c\uc6d0, \ud5c8\ube0c\ub098\ub77c'), STRINGDECODE(' - \uc804\ub098\ubb34\uc232 \uc0dd\ud0dc\uccb4\ud5d8\n - \uc6d4\uc815\uc0ac, \uc0c1\uc6d0\uc0ac \uc5ed\uc0ac\ubb38\ud654 \uccb4\ud5d8\n - \ub300\uad00\ub839 \uc591\ub5bc\ubaa9\uc7a5 \uccb4\ud5d8 \n - \uc2e0\uc7ac\uc0dd\uc5d0\ub108\uc9c0\uad00 \uccb4\ud5d8\n - \ud55c\uad6d\uc790\uc0dd\uc2dd\ubb3c\uc6d0 \uccb4\ud5d8\n - \ud5c8\ube0c\ub098\ub77c \uccb4\ud5d8'), STRINGDECODE('(1\ubc152\uc77c)\uc790\uc5f0\uc73c\ub85c \ub5a0\ub098\ub294 \ud589\ubcf5\uc5ec\ud589'));
