('prg-30', STRINGDECODE('\ubbf8\ub798\uc0ac \ud3b8\ubc31\uc232 \ud0d0\ubc29, \uce5c\ud658\uacbd \uba39\uac70\ub9ac \uccb4\ud5d8, \uc790\uc5f0\ud574\uc124 \ud504\ub85c\uadf8\ub7a8 \uc9c4\ud589, \uc544\ud1a0\ud53c \uad00\ub828 \uc804\ubb38\uac15\uc758 \ub4f1'), STRINGDECODE(' \ubbf8\ub798\uc0ac \ud3b8\ubc31\uc232 \ud0d0\ubc29, \uce5c\ud658\uacbd \uba39\uac70\ub9ac \uccb4\ud5d8, \uc790\uc5f0\ud574\uc124 \ud504\ub85c\uadf8\ub7a8 \uc9c4\ud589, \uc544\ud1a0\ud53c \uad00\ub828 \uc804\ubb38\uac15\uc758 \ub4f1 \uc544\ud1a0\ud53c \ud658\uc544 \ubc0f \ubd80\ubaa8\ub2d8\ub4e4\uc744 \uc704\ud55c \uc720\uc6a9\ud55c \ub0b4\uc6a9\uc73c\ub85c \uaf49 \ucc44\uc6cc\uc838 \uc788\uc2b5\ub2c8\ub2e4. '), STRINGDECODE('\uad6d\ub9bd\uacf5\uc6d0\uacfc \ud568\uaed8\ud558\ub294 \uac74\uac15\ub098\ub204\ub9ac\ucea0\ud504')),
('prg-31', STRINGDECODE('\ud1b5\uc601 \ubbf8\ub975\uc0b0, \ub2ec\uc544\uc804\ub9dd\ub300 \ub4f1'), STRINGDECODE(' \uc9c0\uc5ed\ud559\uad50 \ubbf8\ub798\uc138\ub300\ub4e4\uacfc \ud568\uaed8 \ud560 \uc218 \uc788\ub294 \uccb4\ud5d8\ud615 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4. \uad6d\ub9bd\uacf5\uc6d0 \uc232\uc5d0\uc11c \ub9cc\ub098\ubcfc \uc218 \uc788\ub294 \uc2dd\ubb3c\uc5d0 \ub300\ud574 \uc54c\uc544\ubcf4\uace0, \ub2ec\uc544\uc804\ub9dd\ub300\uc5d0\uc11c \uc12c \uc774\uc57c\uae30\uc5d0 \ub300\ud574 \ub4e4\uc5b4\ubcfc\uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. '), STRINGDECODE('\uc9c0\uc5ed\ud559\uad50 \ubbf8\ub798\uc138\ub300\uc640 \ud568\uaed8\ud558\ub294 \uc0dd\ud0dc\uad00\uad11')),
('prg-32', STRINGDECODE('1\uc77c\ucc28-\uc9c4\uc8fc(\uad6d\ub9bd\uc9c4\uc8fc\ubc15\ubb3c\uad00 \ub4f1), \ud1b5\uc601(\ud55c\uc0b0\ub3c4 \ub4f1). 2\uc77c\ucc28-\ud1b5\uc601(\ud55c\uc0b0\ub3c4 \uc81c\uc2b9\ub2f9 \ub4f1), 3\uc77c\ucc28-\ud1b5\uc601(\ucf00\uc774\ube14\uce74 \ub4f1)'), STRINGDECODE(' \uc784\uc9c4\uc65c\ub780\uacfc \ud55c\uc0b0\ub300\ucca9 \uadf8\ub9ac\uace0 \ud55c\uc0b0\ub3c4 \uc81c\uc2b9\ub2f9, \uadf8 \uc5ed\uc0ac\uc758 \ud604\uc7a5\uc5d0\uc11c \ub4e3\ub294 \uc0dd\uc0dd\ud55c \uc774\uc57c\uae30.\n \n \uc5b4\ub290\uc0c8 \uc2dc\uac04\uc774 \ud6cc\uca4d~ \uc804\ud1b5\uc5b4\uc2dc\uc7a5\uc744 \ub3cc\uc544\ubc84\uace0, \uc9c1\uc811 \uc5b4\ubd80\uac00 \ub418\uc5b4\ubcf4\ub294 \uccb4\ud5d8!\n \n \ud55c\ub824\uc218\ub3c4\uc758 \uc804\uacbd\uc744 \ud55c\ub208\uc5d0 \ub2f4\uc544\uac08\uc218 \uc788\ub294 \ucf00\uc774\ube14\uce74 \uccb4\ud5d8 \ub4f1 \uc54c\uc9dc\ucf54\uc2a4\ub85c \uad6c\uc131\ub418\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. '), STRINGDECODE('[\uc218\ud559\uc5ec\ud589, 2\ubc153\uc77c]\uc784\uc9c4\uc65c\ub780 A to Z, \ud1b5\uc601 \ubb3c\ube5b \uc5ec\ud589')),
('prg-33', STRINGDECODE('\ud55c\ub824\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0 \uc54c\uc544\ubcf4\uae30, \uac2f\ubc8c\uc758 \uc774\ud574, \uac2f\ubc8c\uc0dd\ubb3c \uc54c\uc544\ubcf4\uae30 \ub4f1'), STRINGDECODE('\ud55c\ub824\ud574\uc0c1\uad6d\ub9bd\uacf5\uc6d0\ub0b4 \uc544\ub984\ub2e4\uc6b4 \uc12c\ub9c8\uc744, \ud559\ub9bc\ub3c4\uc5d0\uc11c \ub9cc\ub098\ubcf4\ub294 \uac2f\ubc8c\uacfc \uadf8\uacf3\uc5d0 \uc0ac\ub294 \uac2f\ubc8c\uce5c\uad6c\ub4e4\uc744 \ub9cc\ub0a0\uc218 \uc788\uc2b5\ub2c8\ub2e4\n (\uc131\uc778 1\uc778-15,000\uc6d0, \uc5b4\ub9b0\uc774-5,000\uc6d0)\n (\u203b \ud604\uc9c0\uc0ac\uc815\uc73c\ub85c \uc778\ud55c \uae08\uc561 \ubcc0\ub3d9\uac00\ub2a5)'), STRINGDECODE('\ud1b5\uc601 \uc12c\ub9c8\uc744\uc5d0\uc11c \ub9cc\ub098\ubcf4\ub294 \uac2f\ubc8c\uce5c\uad6c\ub4e4')),
('prg-34', STRINGDECODE('\uce60\uc120\uacc4\uace1 \uc62c\ub77c\uac00\uae30(\ucd94\uc131\uc8fc\ucc28\uc7a5 \u25b6 \ube44\uc120\ub2f4 \u25b6 \uce60\uc120\ud3ed\ud3ec \u25b6  \ucc9c\uc655\ubd09 / 9.7km)'), STRINGDECODE(' \uc790\uc5f0\uacfc \uc2dc\uac04\uc774 \uc2dc\uc791\ub418\ub294 \uacf3 \n<BR>\uc9c0\ub9ac\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uce60\uc120\uacc4\uace1\uc758 \n<BR>\uc6d0\uc2dc\ub9bc\uc744 \ub9cc\ub07d\ud558\uba70 \uac77\ub294 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uc790\uc5f0\uc744 \uac77\ub2e4...\uc6d0\uc2dc\ub9bc\uc744 \uac04\uc9c1\ud55c \uce60\uc120\uacc4\uace1 (\uc62c\ub77c\uac00\uae30)')),
('prg-35', STRINGDECODE('\ud574\uc778\uc0ac\uc640 \ud314\ub9cc\ub300\uc7a5\uacbd \uc5ed\uc0ac\ubb38\ud654\ud574\uc124, \uac00\uc57c\uc0b0\uc18c\ub9ac\uae38 \uac77\uae30 \ubc0f \uc790\uc5f0\ud574\uc124, \ud314\ub9cc\ub300\uc7a5\uacbd \uc778\uacbd\uccb4\ud5d8 \ub4f1'), STRINGDECODE(' \uc720\ub124\uc2a4\ucf54 \uc138\uacc4\uae30\ub85d\uc720\uc0b0 \ud314\ub9cc\ub300\uc7a5\uacbd\uacfc \ud574\uc778\uc0ac\ub97c \ud488\uc740 \uac00\uc57c\uc0b0\uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc990\uae30\ub294 \uc2e0\ub098\ub294 \uc218\ud559\uc5ec\ud589\uc73c\ub85c \uc5ec\ub7ec\ubd84\uc744 \ucd08\ub300\ud569\ub2c8\ub2e4.'), STRINGDECODE('\uc2e0.\ubc14.\ub78c \uc218\ud559\uc5ec\ud589! \uac00\uc57c\uc0b0\uad6d\ub9bd\uacf5\uc6d0 (\ud569\ucc9c, \ud1b5\uc601, \uace0\ub839)'));
INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-36', STRINGDECODE('\ud574\uc778\uc0ac \uc5ed\uc0ac\ubb38\ud654 \ud574\uc124(\ud314\ub9cc\ub300\uc7a5\uacbd\ud310 \ud0c1\ubcf8\uccb4\ud5d8), \uc804\ud1b5\uc778\uc808\ubbf8 \ub9cc\ub4e4\uae30\uccb4\ud5d8'), STRINGDECODE(' \uc5ed\uc0ac\ubb38\ud654\ud574\uc124 \ubc0f \ud314\ub9cc\ub300\uc7a5\uacbd \uc778\uacbd\uccb4\ud5d8,\n <BR>\uc804\ud1b5\uc778\uc808\ubbf8 \ub9cc\ub4e4\uae30 \uccb4\ud5d8\ub4f1\uc73c\ub85c \uad6c\uc131'), STRINGDECODE('\uac00\uc57c\uc0b0\uc5ed\uc0ac\ubb38\ud654 \uccb4\ud5d8 100\ubc30 \uc990\uae30\uae30')),
('prg-37', STRINGDECODE('\uc790\uc5f0\uc18d\uc5d0\uc11c \ub180\uc790! \ud790\ub9c1 \ucea0\ud504'), STRINGDECODE(' \ucd5c\uadfc \ud658\uacbd\uc131\uc9c8\ud658\uc774 \uc99d\uac00\ub428\uc5d0 \ub530\ub77c \ud658\uacbd\uc624\uc5fc\uc774 \uc801\uc740 \uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc790\uc5f0\uc0dd\ud65c \uccb4\ud5d8\uae30\ud68c\ub97c \uc81c\uacf5\ud558\uc5ec \uc544\uc774\ub4e4\uc758 \uc2a4\ud2b8\ub808\uc2a4\ub97c \uc904\uc774\uace0 \uc2a4\uc2a4\ub85c \uc774\uaca8\ub0bc \uc218 \uc788\ub2e4\ub294 \uc790\uc2e0\uac10\uacfc \uc758\uc9c0\ub97c \uac16\uac8c\ud558\ub294 \uc790\uc5f0\uce58\uc720 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uac74\uac15\ub098\ub204\ub9ac \ucea0\ud504')),
('prg-38', STRINGDECODE('\uc0ac\ud68c\ud0d0\uad6c \uad50\uacfc\uacfc\uc815\uc744 \uc911\uc2ec\uc73c\ub85c \uacbd\uc8fc\uc758 \ubb38\ud654\uc720\uc801 \ud604\uc7a5\ub2f5\uc0ac\uc640 \uc774\ub860 \uad50\uc721'), STRINGDECODE(' \uc0ac\ud68c\ud0d0\uad6c \uad50\uacfc\uacfc\uc815\uc744 \uc911\uc2ec\uc73c\ub85c \uacbd\uc8fc\uc758 \ubb38\ud654\uc720\uc801 \ud604\uc7a5\ub2f5\uc0ac\uc640 \uc774\ub860\uad50\uc721, \ubb38\ud654\uc7ac\ub97c \ud65c\uc6a9\ud55c \uccb4\ud5d8\ud504\ub85c\uadf8\ub7a8 \uac1c\ubc1c \ub4f1 \uc785\uccb4\uc801 \ud559\uc2b5\uc73c\ub85c \uc2e0\ub77c\uc758 \ubb38\ud654\uc640 \uc815\uc2e0\uc5d0 \ub300\ud55c \uc804\ubb38\uc131\uc744 \ud568\uc591\ud558\uc5ec \uad50\uc721\uacfc\uc815\uc5d0 \uc811\ubaa9\ud568\uc744 \ubaa9\uc801\uc73c\ub85c \ud568'), STRINGDECODE('\uc0ac\ud68c\ud0d0\uad6c \uad50\uc721\uacfc\uc815\uc744 \uc811\ubaa9\ud55c \uacbd\uc8fc\uc5ed\uc0ac\ubb38\ud654\ud0d0\ubc29')),
('prg-39', STRINGDECODE('\uc138\uacc4\ubb38\ud654\uc720\uc0b0\uc744 \ucc3e\uc544 \ub5a0\ub098\ub294 \uc5ec\ud589'), STRINGDECODE(' \uc790\uc5f0\ub098\ub204\ub9ac\ucea0\ud504\ub97c \ud1b5\ud574 \uacbd\uc8fc\uc640 \uc11c\uc6b8\uc758 \uc138\uacc4\ubb38\ud654\uc720\uc0b0\uc744 \ubc30\uc6b0\uace0 \uad6d\ub9bd\uacf5\uc6d0\uc758 \uc790\uc5f0\ub180\uc774\ub97c \ud1b5\ud574 \uc7ac\ubbf8\uc640 \uc790\uc5f0\uc774 \uc8fc\ub294 \uc774\ub85c\uc6c0\uae4c\uc9c0 \ub3d9\uc2dc\uc5d0 \uc811\ud574\ubcf4\ub294 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uc790\uc5f0\ub098\ub204\ub9ac \ucea0\ud504')),
('prg-40', STRINGDECODE('\ubd88\uad50 \uc720\uc801 \ubcf4\ubb3c\ucc3d\uace0 \ub0a8\uc0b0\uc720\uc801\uc9c0\uad6c, \ubba4\uc9c0\uceec \ubbf8\uc18c2[''\uc2e0\uad6d\uc758 \ub545, \uc2e0\ub77c''], \uadfc\ub300\ubb38\ud654\uc720\uc0b0\uc758 \uac70\ub9ac, \ubc29\uc9dc\uc720\uae30\ubc15\ubb3c\uad00'), STRINGDECODE('\uc2e0\ub77c \ucc9c\ub144 \ubd88\uad50\uc720\uc801\uc9c0\uc640 \ub0a8\uc0b0 \uad6d\ub9bd\uacf5\uc6d0\ud2b8\ub808\ud0b9, \uadfc\ub300 \ubb38\ud654\uc720\uc0b0\uc758 \ubcf4\uace0 \"\ub300\uad6c \uace8\ubaa9\uae38 \ud22c\uc5b4\", \uc2dc\uc778 \uc774\uc0c1\ud654 \uc120\uc0dd\uc758 \uace0\ud0dd\uacfc \uc790\ucde8, 3.1\uc6b4\ub3d9 \uacc4\ub2e8\uc5d0\uc11c \ud798\ucc28\uac8c \uc678\uccd0\ubcf4\ub294 \"\ub300\ud55c\ub3c5\ub9bd\ub9cc\uc138!\"\uac00 \uac00\uc2b4\uc744 \ubb49\ud074\ud558\uac8c \ud569\ub2c8\ub2e4.'), STRINGDECODE('[\uc218\ud559\uc5ec\ud589] \uc2e0\u318d\ubc14\u318d\ub78c \uacbd\uc8fc\uad6d\ub9bd\uacf5\uc6d0')),
('prg-41', STRINGDECODE('\uc138\uacc4\ubb38\ud654\uc720\uc0b0\uc5d0 \ub4f1\uc7ac\ub41c \uacbd\uc8fc\uc758 \ubb38\ud654\uc7ac \ud0d0\ubc29'), STRINGDECODE(' \uc678\uad6d\uc778\ub4e4\ub3c4 \ud3b8\uc548\ud558\uace0 \ud6a8\uc728\uc801\uc73c\ub85c \uc138\uacc4\uc801\uc778 \ubb38\ud654\uad00\uad11\ub3c4\uc2dc\uc778 \uacbd\uc8fc\ub97c \uc5ec\ud589\ud560 \uc218 \uc788\uac8c \ud558\uace0, \ud55c\uad6d\uc758 \uc544\ub984\ub2e4\uc6b4 \uc804\ud1b5\ubb38\ud654\ub97c \uccb4\ud5d8\ud560 \uc218 \uc788\uac8c \ud568\uc73c\ub85c\uc368 \uacbd\uc8fc\uad6d\ub9bd\uacf5\uc6d0\uc744 \uc138\uacc4\uc801\uc73c\ub85c \uc54c\ub9ac\uace0\uc790 \ud569\ub2c8\ub2e4.'), STRINGDECODE('\uc138\uacc4\uc18d\uc758 \uacbd\uc8fc'));
INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-42', STRINGDECODE('1\uc77c\ucc28 : \ubd80\uc11d\uc0ac, \uc18c\uc218\uc11c\uc6d0, \uc120\ube44\ubb38\ud654\uccb4\ud5d8, 2\uc77c\ucc28 : \uc8fd\ub839\uc61b\uae38, \ub3c4\ub2f4\uc0bc\ubd09, \ud55c\ub4dc\ubbf8 \ub9c8\uc744(\ub18d\ucd0c\uccb4\ud5d8) 3\uc77c\ucc28 : \ub2e8\uc591 \uc120\uc554\uacc4\uace1 \uc0ac\uc778\uc554, \ucda9\uc8fc\ud638 \uc720\ub78c\uc120 \uad00\uad11'), STRINGDECODE('1\uc77c\ucc28 : \ubd88\uad50 \ubb38\ud654\uc640 \uc720\uad50 \ubb38\ud654\uc758 \uc911\uc2ec\uc758 \ud504\ub85c\uadf8\ub7a8 \uc9c4\ud589\n 2\uc77c\ucc28 : \ubc31\ub450\ub300\uac04\uc5d0 \uae43\ub4e0 \uc804\ud1b5 \ubb38\ud654\uccb4\ud5d8\n 3\uc77c\ucc28 : \ub2e8\uc591 8\uacbd \uc18d\uc5d0\uc11c \uc2e0\uc120\uc774 \ub418\ub2e4!(\ub2e8\uc591 8\uacbd, \ucda9\uc8fc\ud638 \uc720\ub78c\uc120)'), STRINGDECODE('\uc218\ud559\uc5ec\ud589_\uc5ed\uc0ac\uc640 \ubb38\ud654\uac00 \uc0b4\uc544 \uc228\uc26c\ub294 \ubc31\ub450\ub300\uac04 \uc720\ub78c \uc5ec\ud589')),
('prg-43', STRINGDECODE('1.\ubd80\uc11d\uc0ac 2.\uc18c\uc218\uc11c\uc6d0(\uc120\ube44\ucd0c) 3.\uc8fd\ub839\uc61b\uae38 4.\uc7a5\uc678\ub098\ub8e8'), STRINGDECODE(' \uc18c\ubc31\uc0b0 \ubc0f \uc778\uadfc\uc5d0 \uc704\uce58\ud55c \uc5ed\uc0ac\ubb38\ud654\uc790\uc6d0 \ub2f5\uc0ac \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uc790\uc5f0\uacfc \uc5ed\uc0ac\uac00 \ud568\uaed8\ud558\ub294 \uc18c\ubc31\uc0b0 \uccb4\ud5d8 \uc5ec\ud589')),
('prg-44', STRINGDECODE('1.\ud76c\ubc29\uacc4\uace1  2.\uc8fd\ub839\uc61b\uae38'), STRINGDECODE(' \uc18c\ubc31\uc0b0 \uc778\uadfc\uc758 \ud76c\ubc29\uacc4\uace1\uc5d0\uc11c \uc790\uc5f0\uc0dd\ud0dc\ub97c, \uc8fd\ub839\uc61b\uae38\uc5d0\uc11c \uc5ed\uc0ac\ubb38\ud654\ub97c \ubc30\uc6b0\ub294 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uce5c\uad6c\ub4e4\uc544 \ub5a0\ub098\uc790! \uc18c\ubc31\uc0b0\uc758 \uc790\uc5f0\uc18d\uc73c\ub85c')),
('prg-45', STRINGDECODE('(1day)\uc548\ub3d9\ud558\ud68c\ub9c8\uc744 - (2day)\uc8fc\uc655\uc0b0\uad6d\ub9bd\uacf5\uc6d0, \uc601\ub355 \ube14\ub8e8\ub85c\ub4dc - (3day)\uc6b8\uc9c4 \uc6d4\uc1a1\uc815, \uc131\ub958\uad74, \ub098\ub77c\uace8 \ubcf4\ub9ac\ub9d0 \uccb4\ud5d8\ub9c8\uc744'), STRINGDECODE(' ''\ud55c\uad6d\uc758 \uadf8\ub79c\ub4dc\uce90\ub10c'' \uc8fc\uc655\uc0b0\uad6d\ub9bd\uacf5\uc6d0, \uc720\ub124\uc2a4\ucf54 \uc138\uacc4\ubb38\ud654\uc720\uc0b0 \uc548\ub3d9\ud558\ud68c\ub9c8\uc744\uc758 \uace0\uc544\ud568\uacfc \uc790\uae0d\uc2ec \uadf8\ub9ac\uace0 \ud478\ub978 \ubc14\ub2e4\uc640 \ud478\ub978 \ud558\ub298\uc774 \ub9de\ub2ff\uc740 \uc601\ub355 \ube14\ub8e8\ub85c\ub4dc \ud2b8\ub808\ud0b9\uc5d0 \ud638\uc5f0\uc9c0\uae30\uac00 \uc808\ub85c \uae38\ub7ec\uc9c4\ub2e4. '), STRINGDECODE('\uc2e0\u00b7\ubc14\u00b7\ub78c \uc218\ud559\uc5ec\ud589 \uc8fc\uc655\uc0b0\uad6d\ub9bd\uacf5\uc6d0')),
('prg-46', STRINGDECODE('\uc8fc\uc655\uc0b0 \ud0d0\ubc29\uc548\ub0b4\uc18c \u2192 \uc810\uc2ec\uc2dd\uc0ac\u2192 \uc8fc\uc655\uc0b0 \ud0d0\ubc29\u2192\uc1a1\uc18c\uace0\ud0dd\u2192\ud55c\uc9c0\uccb4\ud5d8\uc7a5\u2192\uc18c\uac10\ub098\ub204\uae30 \ubc0f \ub9c8\ubb34\ub9ac'), STRINGDECODE(' \uccad\uc815\uc9c0\uc5ed \uc8fc\uc655\uc0b0\uc0dd\ud0dc\uad00\uad11\uc5d0 \ucc38\uc5ec\ud558\uc2dc\uba74!\n <BR> \uc5ed\uc0ac\uc640 \ubb38\ud654, \uc6f0\ube59\uad00\uad11\uc744 \ub9d8\uaecf \uc990\uae38 \uc218 \uc788\uc2b5\ub2c8\ub2e4.'), STRINGDECODE('\uc790\uc5f0\uacfc \ud568\uaed8\ud558\ub294 \uc6b0\ub9ac\ubb38\ud654 \ud0d0\ubc29')),
('prg-47', STRINGDECODE('\uc8fc\uc655\uc0b0 \ud0d0\ubc29\uc548\ub0b4\uc18c \u2192 \uc8fc\uc655\uc0b0\ub098\ub4e4\uc774 \u2192 \uc1a1\uc18c\uace0\ud0dd \u2192 \uc124\ubb38\uc870\uc0ac \ubc0f \ub290\ub08c \ub098\ub204\uae30'), STRINGDECODE(' \uccad\uc815\uc9c0\uc5ed \uc8fc\uc655\uc0b0\uc0dd\ud0dc\uad00\uad11\uc5d0 \ucc38\uc5ec\ud558\uc2dc\uba74!\n <BR>\uc790\uc5f0\uc744 \uac10\uc0c1\ud558\uace0 \ubc30\uc6b0\uba70 \uc5ed\uc0ac\uc640 \ubb38\ud654, \uc6f0\ube59\uad00\uad11\uc744 \ub9d8\uaecf \uc990\uae38 \uc218 \uc788\uc2b5\ub2c8\ub2e4*^^*'), STRINGDECODE('\uc0ac\uacfc\ud5a5\uae30 \uac00\ub4dd\ud55c \uc8fc\uc655\uc0b0 \ub18d\ucd0c\uccb4\ud5d8')),
