INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-86', STRINGDECODE('\ubaa8\ub450\ubaa8\ub450\ubaa8\uc5ec\uc694~\uae08\uac15\ubaa8\uce58\uac00 \uc0b4\uace0\uc788\ub294 \uad6c\ucc9c\ub3d9\uacc4\uace1\uc774\uc57c\uae30~\uac00\uc871\ub808\ud06c\ub808\uc774\uc158~\uad00\uad11\uace4\ub3cc\ub77c~\ud5a5\uc801\ubd09~\ub9c9\uac78\ub9ac \ub9cc\ub4e4\uae30 \uccb4\ud5d8'), STRINGDECODE('\uc232\uc18d\uc5ec\ud589\uc744 \ud558\uba70 1\ubc152\uc77c\ub3d9\uc548 \uc544\ub984\ub2e4\uc6b4 \uc790\uc5f0\uc744 \uc990\uaca8\ubcfc \uc218 \uc788\ub294 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.\n \ub2e8\uccb4\ucc38\uac00\ub294 \uc544\ub798\uc804\ud654\ub85c \ubb38\uc758\ud574\uc8fc\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4.\n <BR>\ubb38\uc758: 063-322-3174, 3473. \uc0dd\ud0dc\uad00\uad11 \ub2f4\ub2f9\uc790'), STRINGDECODE('\ub355\uc720\uc0b0 \uc232\uc18d\uc5ec\ud589')),
('prg-87', STRINGDECODE('\ub355\uc720\uc0b0\uad6d\ub9bd\uacf5\uc6d0 \uc232\uc18d \ud0d0\ubc29-\uae08\uac15 \ub808\ud504\ud305-\ub9c8\uc774\uc0b0 \uc5ed\uc554\ub3d9\uad74 \ud0d0\ubc29-\ubcc0\uc0b0\ubc18\ub3c4\uad6d\ub9bd\uacf5\uc6d0 \uac2f\ubc8c \ud0d0\ubc29'), STRINGDECODE('\ubc31\ub450\ub300\uac04\uc758 \ub4f1\uc904\uae30 \ub355\uc720\uc0b0\uc5d0\uc11c \ubed7\uc5b4\uc838 \ub0b4\ub824\uc628 \uc0b0\uc904\uae30\uc640 \uac15\uc904\uae30\ub294 \ub9c8\uc774\uc0b0\uc5d0\uc11c \ub9cc\ub098 \uc0b0\ud0dc\uadf9 \uc218\ud0dc\uadf9\uc744 \ubd88\ub7ec\uc654\uc2b5\ub2c8\ub2e4. 1\uc5b5 \ub144 \uc804\uc758 \ud638\uc218\uc600\ub358 \uc2e0\ube44\ub85c\uc6b4 \ub9c8\uc774\uc0b0\uc5d0\uc11c \ub3cc\ud0d1\uacfc   \uc5ed\uace0\ub4dc\ub984 \uc774\uc57c\uae30\ub294 \uc9c1\uc811 \ubcf4\uc544\uc57c\ub9cc \ub290\ub084 \uc218 \uc788\ub294 \uc2e0\ube44\ud568\uc744 \uac04\uc9c1\ud55c \uc790\uc5f0\uc0dd\ud0dc\uccb4\ud5d8\uc785\ub2c8\ub2e4.   \n \uc804\uc8fc\ub294 \uc870\uc120\uc655\uc870 500\ub144\uc758 \ubc1c\uc0c1\uc9c0\uc774\uba70 \uc74c\uc2dd\ubb38\ud654\uac00 \ubc1c\ub2ec\ud558\uace0 \uc804\ud1b5\uc774 \uc0b4\uc544 \uc228 \uc26c\ub294   \uc628\uace0\uc9c0\uc2e0\uc758 \uc804\ud1b5\ub3c4\uc2dc\uc5ec\ud589\uc785\ub2c8\ub2e4. \uc774\uc640 \ub354\ubd88\uc5b4 \uc11c\ud574\uc548 \uac2f\ubc8c\uc5ec\ud589\uc740 \ud574\uc591 \uc0dd\ud0dc\uacc4\uc758 \uc0dd   \uc131\uc9c0\uc774\uace0 \ubc14\ub2f7\ubb3c\uacfc \ud759\uc744 \ube5a\uc5b4 \uc0c8\ub85c\uc6b4 \uc790\uc6d0\ud0c4\uc0dd\uc758 \uc18c\uc911\ud568\uc744 \ub290\ub07c\uac8c \ud574\uc8fc\ub294 \ud0d0\ud5d8\uc5ec\ud589   \uc785\ub2c8\ub2e4.'), STRINGDECODE('[\uc218\ud559\uc5ec\ud589]\uae38\uc5d0\uc11c \ub180\uba70 \ubc30\uc6b0\ub294 \uc790\uc5f0\ud559\uad50\uc5ec\ud589')),
('prg-88', STRINGDECODE('\ubcc0\uc0b0\ubc18\ub3c4 \uad6d\ub9bd\uacf5\uc6d0\uc758 \uc544\ub984\ub2e4\uc6b4 \uc0b0\uacfc \ubc14\ub2e4 \uadf8\ub9ac\uace0 \uc9c0\uc5ed\uc8fc\ubbfc\uc758 \uc0dd\ud65c\ubb38\ud654 \uccb4\ud5d8 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE(' \uc0b0\uacfc \ubc14\ub2e4\uac00 \uc544\ub984\ub2e4\uc6b4 \ubcc0\uc0b0\ubc18\ub3c4\uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc232\uccb4\ud5d8, \ubc14\ub2e4\uccb4\ud5d8\uacfc \ub354\ubd88\uc5b4 \ubc14\ub2e4\uc640 \ub4e4\uc744 \uc8fc \uc0dd\ud65c\uc9c0\ub85c \uc0b4\uc544\uac00\ub294 \uc9c0\uc5ed\uc8fc\ubbfc\ub4e4\uc758 \uc0dd\ud65c\ubb38\ud654 \ub4f1 \ub2e4\uc591\ud55c \uccb4\ud5d8\uc704\uc8fc\uc758 \ud504\ub85c\uadf8\ub7a8'), STRINGDECODE('\uc0b0\uacfc \ubc14\ub2e4\uac00 \ud568\uaed8\ud558\ub294 \uc544\ub984\ub2e4\uc6b4 \ub3d9\ud589')),
('prg-89', STRINGDECODE('\ub0b4\uc7a5\uc0b0 \uc790\uc5f0 \uc18d \ud2b8\ub808\ud0b9\uacfc \ud568\uaed8 \ubbf8\uc158\uccb4\ud5d8\ub3c4 \uc990\uae30\uc2dc\uace0 100\ub144 \uc804 \ucd08\uac00\ub9c8\uc744\uc5d0\uc11c \ud558\ub8fb\ubc24\uc744 \ubcf4\ub0bc \uc218  \uc788\ub294 \uc219\ubc15\ud615 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.'), STRINGDECODE(' - \ub0b4\uc7a5\uc0b0 \ud0d0\ubc29\uc548\ub0b4\uc18c \uad00\ub78c\n - \uc790\uc5f0\ub180\uc774 \ubc0f \ub098\ubb47\uc78e\ud2f0\uc154\uce20 \ub9cc\ub4e4\uae30 \uccb4\ud5d8\n - \uc232 \ud2b8\ub808\ud0b9, \ubbf8\uc158\uc218\ud589 \n - \ucd08\uac00\uc9d1\uc5d0\uc11c \ubcf4\ub0b4\ub294 \ubc24\n   (\uc5ec\uce58\uc9d1\ub9cc\ub4e4\uae30 \uccb4\ud5d8 \ub4f1)\n - \uac15\uc815 \ub9cc\ub4e4\uae30 \uccb4\ud5d8'), STRINGDECODE('\ub0b4\uc7a5\uc0b0\uacfc \ud568\uaed8\ud558\ub294 \uc990\uac70\uc6b4 \uc5ec\ud589(\uc219\ubc15\ud615)'));
