('prg-92', STRINGDECODE('\ucda9\ub0a8 \uacf5\uc8fc \ub3d9\ud559\uc0ac \uc9c0\uc5ed \uc77c\uc6d0'), STRINGDECODE(' \uacc4\ub8e1\uc0b0 \ub3d9\ud559\uc0ac\uc9c0\uad6c\uc758 \uc790\uc5f0\uacfc \ubb38\ud654\ub97c \uccb4\ud5d8\ud558\uace0 \uc778\uadfc\uc758\n \uad00\uad11\uc9c0\ub97c \uad00\ub78c\ud558\ub294 \ud0d0\ubc29\ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.'), STRINGDECODE('\ubbf8\ub798\uc138\ub300 \uacc4\ub8e1\uc0b0 \uc0dd\ud0dc\ud0d0\ubc29')),
('prg-93', STRINGDECODE('\ucda9\ub0a8 \uacc4\ub8e1\uc0b0 \ub3d9\ud559\uc0ac \uc9c0\uc5ed \ubc0f \uacf5\uc8fc, \ubd80\uc5ec \uc77c\uc6d0'), STRINGDECODE(' \uacc4\ub8e1\uc0b0 \ub3d9\ud559\uc0ac\uc9c0\uad6c\uc758 \uc790\uc5f0\uacfc \ubb38\ud654\ub97c \uccb4\ud5d8\ud558\uace0 \uc778\uadfc\uc758 \uacf5\uc8fc, \ubd80\uc5ec \uad00\uad11\uc9c0\ub97c \ud0d0\ubc29\ud558\ub294 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.'), STRINGDECODE('\ub808\uc778\uc800\uc640 \ud568\uaed8\ud558\ub294 \uacc4\ub8e1\uc0b0 \uc0dd\ud0dc\ud0d0\ubc29')),
('prg-94', STRINGDECODE('\uacc4\ub8e1\uc0b0~\uacf5\uc8fc~\uc11c\ud574 \ubc14\ub2e4~\ubd80\uc5ec. 2\ubc15 3\uc77c\ud615 \uc218\ud559\uc5ec\ud589 \ud504\ub85c\uadf8\ub7a8.'), STRINGDECODE(' \ubc31\uc81c \uc5ed\uc0ac\ub97c \ub300\ud45c\ud558\ub294 \uacc4\ub8e1\uc0b0, \uacf5\uc8fc, \ubd80\uc5ec \uc9c0\uc5ed\uc744 \ub3cc\uc544\ubcf4\uba70 \ubc31\uc81c\uc778\uc758 \uc228\uacb0\uc744 \ub290\ub07c\ub294 \uc5ed\uc0ac \ud0d0\ubc29 \ud504\ub85c\uadf8\ub7a8(\uc218\ud559\uc5ec\ud589)'), STRINGDECODE('\uacc4\ub8e1\uc0b0 \ub530\ub77c \ubc31\uc81c\ud5a5\uae30 eco-tour')),
('prg-95', STRINGDECODE('\uad6d\ub9bd\uacf5\uc6d0\uacfc \ud568\uaed8\ud558\ub294 \uac74\uac15 \ub098\ub214 \ucea0\ud504!! \ud658\uacbd\uc131\uc9c8\ud658 \uc27d\uac8c \uc774\ud574\ud558\uae30, \uc790\uc5f0\ub180\uc774\ud398\uc2a4\ud2f0\ubc8c, \ud0dc\uc548\ud574\ubcc0\uae38 \uc5d0\ucf54\uc5d4\ud2f0\uc5b4\ub9c1\uc744 \ud568\uaed8 \uc990\uae30\uba70 \ubab8\uacfc \ub9c8\uc74c\uc744 \uce58\uc720\ud558\ub294 \uc2dc\uac04\uc744 \ud568\uaed8 \ud569\ub2c8\ub2e4.'), STRINGDECODE(' \ud478\ub978 \uacf0\uc194\uc232\uacfc \ud30c\ub780 \ubc14\ub2e4\uac00 \uc5b4\uc6b0\ub7ec\uc9c4 \ud0dc\uc548\ud574\uc548\uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \ud658\uacbd\uc131\uc9c8\ud658(\ucc9c\uc2dd)\uc5d0 \ub300\ud55c \uc62c\ubc14\ub978 \uc774\ud574\ub97c \ub3d5\uace0, \ud0dc\uc548\ud574\ubcc0\uae38 \ud2b8\ub808\ud0b9\uc744 \ud1b5\ud574 \uac74\uac15\uc744 \ub2e4\uc9c0\uace0, \ub2e4\uc591\ud55c \uc790\uc5f0\ub180\uc774\ub97c \ud1b5\ud574 \uce58\uc720\uc758 \uc2dc\uac04\uc744 \ud568\uaed8\ud558\ub294 \uac74\uac15\ub098\ub214 \ucea0\ud504'), STRINGDECODE('\ud478\ub978\uc232 \ud30c\ub780\ubc14\ub2e4 \uac74\uac15\ub098\ub204\ub9ac \ucea0\ud504')),
('prg-96', STRINGDECODE('\uad7d\uc774\uad7d\uc774 230km \ub9ac\uc544\uc2a4\uc2dd \ud574\uc548\uc120\uc758 \uc544\ub984\ub2e4\uc6c0\uc744 \ud488\uc740 \ud0dc\uc548\ud574\uc548\uad6d\ub9bd\uacf5\uc6d0 \uc0dd\ud0dc\ud0d0\ubc29!! \u300elet''s go \ud0dc\uc548\ud574\uc548 \uc0dd\ud0dc\ub85c\ub4dc\u300f 100\ubc30 \uc990\uae30\uae30!!'), STRINGDECODE('\ud0dc\uc548\ud574\uc548\uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc6b4\uc601\ub418\ub294 \uc0dd\ud0dc\uad00\uad11 \u300eLet''s go \ud0dc\uc548\ud574\uc548 \uc0dd\ud0dc\ub85c\ub4dc\u300f\ub294 \ubaa8\ub798\uc5b8\ub355\uc774 \uac04\uc9c1\ud55c \ud574\uc548\uc0ac\uad6c\uc758 \ube44\ubc00 \ud0d0\ud5d8\uacfc \ub354\ubd88\uc5b4 \ubb3c\ub54c\uc5d0 \ub9de\ucdb0 \ub4dc\ub7ec\ub098\ub294 \uc2e0\ube44\ud55c \ubc14\ub2f7\uac00\uc758 \uc0dd\ubb3c\ub4e4\uc744 \uc9c1\uc811 \ub9cc\ub098\uace0 \uad00\ucc30\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc5f4\uc545\ud55c \ud658\uacbd \uc18d\uc5d0\uc11c\ub3c4 \uafcb\uafcb\uc774 \uc0b4\uc544\uac00\ub294 \ud76c\uadc0\uc0dd\ubb3c\uc744 \uad00\ucc30\ud558\uace0 \uadf8\ub4e4\uc758 \uc0dd\uc874\uc804\ub7b5\uc744 \ubc30\uc6b0\uba70 \uce5c\uad6c\uac00 \ub420 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \n \ud0dc\uc548\ud574\uc548\uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc6b4\uc601\ub418\ub294 \u300eLet''s go \ud0dc\uc548\ud574\uc548 \uc0dd\ud0dc\ub85c\ub4dc\u300f\uc740 \uc5f0\uc778, \uac00\uc871 \ub204\uad6c\ub098 \ubd80\ub2f4 \uc5c6\uc774 \ucc38\uc5ec\ud558\uc2e4 \uc218 \uc788\ub294 \uad6d\ub9bd\uacf5\uc6d0 \ucd5c\uace0\uc758 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4. \uc6b4\uc601\uac1c\uc694 \n 1) \uc0ac\uc804\uc608\uc57d \ud544\uc218(www.ecotour.knps.or.kr)\n 2) \ub2f9\uc77c\ud615\n 3) \ubaa8\uc9d1\uc778\uc6d0 20\uc778 \uc774\uc0c1 \uc2dc \uc6b4\uc601\uac00\ub2a5\n 4) \uc6b4\uc601\ube44 \uac1c\uc778 \ubd80\ub2f4(\ucc28\ub7c9\uc784\ub300, \uc911\uc2dd, \uc5ec\ud589\uc790\ubcf4\ud5d8, \uccb4\ud5d8\ube44 \ub4f1)\n 5) \uc900\ube44\ubb3c : \ud3b8\uc548\ud55c \uc637\ucc28\ub9bc, \uc6b4\ub3d9\ud654, \ubaa8\uc790, \uac2f\ubc8c\uccb4\ud5d8\uc2dc \uc5ec\ubc8c\uc758 \uc637 \ub4f1'), STRINGDECODE('let`s go  \ud0dc\uc548\ud574\uc548 \uc0dd\ud0dc\ub85c\ub4dc'));
INSERT INTO "PUBLIC"."PROGRAM" VALUES
('prg-97', STRINGDECODE('\ubc31\uc81c\uc758 \ubb38\ud654\uc640 \ubc14\ub2e4 \uc0dd\ud0dc\uacc4\uc758 \uc624\uac10\uc744 \uccb4\ud5d8\ud560 \uc218 \uc788\ub294 \ud0dc\uc548\ud574\uc548 \uc218\ud559\uc5ec\ud589'), STRINGDECODE('  \u25cb \ucc9c\ub144\uc744 \uc774\uc5b4\uc628 \ubc31\uc81c\uc758 \ubbf8\uc18c\n \ube5b\uc5d0 \ub530\ub77c \uc6c3\ub294 \ud45c\uc815\uc774 \ub2ec\ub77c\uc9c0\ub294 \ubc31\uc81c\uc758 \ucc9c\ub144 \ubbf8\uc18c \uc11c\uc0b0 \ub9c8\uc560\uc0bc\uc874\ubd88\uc0c1\uc744 \ubcf4\uba70 \ubb38\ud654\uc640 \uc5ed\uc0ac\uc5d0 \ub300\ud574 \ubc30\uc6b8 \uc218 \uc788\uace0, \uac1c\uc2ec\uc0ac\uc5d0 \ub4e4\ub824 \ub9c8\uc74c\uac00\uc9d0\uc744 \uc815\ub9ac\ud560 \uc218 \uc788\ub294 \uc2dc\uac04\uc744 \ubcf4\ub0b4\uc790,\n \n \u25cb \ubc14\ub2e4 \uc0dd\ud0dc\uacc4\uc758 \uc624\uac10\uc744 \ub290\ub084 \uc218 \uc788\ub294 \uc5ec\ud589\n \uc0b4\uc544\uc788\ub294 \uc0dd\uba85\uc758 \ubc8c, \uac2f\ubc8c. \ud0dc\uc548\uc758 \uc0b4\uc544\uc788\ub294 \uc0dd\ud0dc\uacc4\ub97c \uc9c1\uc811 \uccb4\ud5d8\ud560 \uc218 \uc788\ub294 \uac2f\ubc8c\uccb4\ud5d8\uacfc \uc5fc\uc804\uccb4\ud5d8\uc744 \ud1b5\ud574, \ud574\uc548 \uc0dd\ud0dc\uacc4\uc758 \uba4b\uc9c4 \ud5a5\uc5f0\uc744 \uac10\uc0c1\ud574\ubcf4\uc790. \ud2b9\ud788 \ud0dc\uc548\ubc14\ub2f7\uae38\uc744 \uc9c1\uc811 \uac78\uc5b4\ubcf4\uba70 \ud574\uc548 \ub3d9, \uc2dd\ubb3c\ub4e4\uc744 \ud55c\ub208\uc5d0 \ubcfc \uc218 \uc788\uc73c\uba70, \ubc14\ub2e4 \uc0dd\ud0dc\uacc4\ub97c \uc624\uac10\uc73c\ub85c \ub290\ub07c\uace0 \uacbd\ud5d8\ud560 \uc218 \uc788\ub2e4.  \n \n \u25cb \ud0dc\uc548 \ud574\uc548 \uad6d\ub9bd\uacf5\uc6d0\uc5d0\uc11c \uc0dd\ud0dc\uac10\uc218\uc131\uc744 \ud0a4\uc6b0\ub294 \uc5ec\ud589\n \uc6b0\ub9ac\ub098\ub77c \uc720\uc77c\uc758 \ud574\uc548\ud615 \uad6d\ub9bd\uacf5\uc6d0\uc778 \ud0dc\uc548\ud574\uc548 \uad6d\ub9bd\uacf5\uc6d0, \ub300\ud45c\uc801\uc778 \ub9ac\uc544\uc2a4\uc2dd \ud574\uc548\uc744 \ubcf4\uace0 \ub300\uc790\uc5f0\uc744 \ub9cc\ub0a0 \uc218 \uc788\ub294 \uacf3\uc774\ub2e4. \uc11c\ub85c \ub2e4\ub978 \uc774\ub984\uc744 \uac00\uc9c4 \ud574\ubcc0\ub4e4\uc774 \uc624\ubc00\uc870\ubc00 \ud574\uc548\uc120\uc744 \uc774\ub8e8\ub294 \uacf3. \uc11c\ud574\uc548\uc758 \uc12c\uc138\ud55c \uc7a5\uad00\uc744 \ub290\uaef4\ubcf4\uc790.\n '), STRINGDECODE('\ubc31\uc81c\uc758 \ubb38\ud654\ub97c \ubc30\uc6b0\uace0 \ubc14\ub2e4 \uc0dd\ud0dc\uacc4\uc758 \uc624\uac10\uc744 \ub290\ub084 \uc218 \uc788\ub294 \uc5ec\ud589')),
('prg-98', STRINGDECODE('1\uc77c\ucc28 : \uc5f0\ud654\ubd09,\uc18c\ubc31\uc0b0\ucc9c\ubb38\ub300 , 2\uc77c\ucc28 : \ub2e8\uc591\ud314\uacbd \ud22c\uc5b4, \ucda9\uc8fc\ud638\uc720\ub78c\uc120 \uc2b9\uc120'), STRINGDECODE(' \uc774 \ud504\ub85c\uadf8\ub7a8\uc740 \uc18c\ubc31\uc0b0\uad6d\ub9bd\uacf5\uc6d0\ubd81\ubd80\uc0ac\ubb34\uc18c \ud2b9\ubcc4\ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.'), STRINGDECODE('\ucc9c\uc0c1\uc758 \ud654\uc6d0 \uc5f0\ud654\ubd09 \uc990\uae30\uae30')),
('prg-99', STRINGDECODE('1.\ud55c\ub4dc\ubbf8\ub9c8\uc744 \ud22c\uc5b4 2.\ub18d\ucd0c\ubb38\ud654\uccb4\ud5d8 3. \uc804\ud1b5\uc74c\uc2dd \ub9cc\ub4e4\uae30 \uccb4\ud5d8 \ub4f1'), STRINGDECODE(' \uad6d\ub0b4 \ucd5c\uace0\uc758 \uc0dd\ud0dc\ub9c8\uc744\uc778 \ud55c\ub4dc\ubbf8\ub9c8\uc744\uc5d0\uc11c \uba38\ubb3c\uba70 \uc77c\uc0c1\uc5d0\uc11c\ub294 \uacbd\ud5d8\ud560 \uc218 \uc5c6\uc5c8\ub358 \ud2b9\ubcc4\ud55c \ub18d\ucd0c \uccb4\ud5d8\uc744 \uc2e0\ub098\uace0 \uc7ac\ubbf8\uc788\uac8c \uacbd\ud5d8\ud560 \uc218 \uc788\ub294 \ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.'), STRINGDECODE('\ud55c\ub4dc\ubbf8\uc57c \ub180\uc790 (\ud31c\uc2a4\ud14c\uc774)')),
('prg-100', STRINGDECODE('\ub300\uba85\ub9ac\uc870\ud2b8 ~ \uc0ac\uc778\uc554 ~  \uc0c1\uc120\uc554 ~ \uc720\ub78c\uc120 \uad00\uad11(\uad6c\ub2f4\ubd09.\uc625\uc21c\ubd09) ~ \ub300\uba85\ub9ac\uc870\ud2b8'), STRINGDECODE(' \uc6d4\uc545\uc0b0 \uc0dd\ud0dc\uad00\uad11 ''\ub300\uba85\ud22c\uc5b4, \uc6d4\uc545\uc0b0\uc5d0 \uc548\uae30\ub2e4.'' \ud504\ub85c\uadf8\ub7a8\uc740 \ub2e8\uc591\uc9c0\uc5ed \ub2e8\uc591\ud314\uacbd \ud574\uc124\uc5d0 \ucc38\uc5ec\ud558\uace0 \uc720\ub78c\uc120 \ud0d1\uc2b9\uc744 \uccb4\ud5d8 \ud560 \uc218 \uc788\ub294 \ub300\uba85\ub9ac\uc870\ud2b8 \ub2e8\uc591 \uc5f0\uacc4\ud504\ub85c\uadf8\ub7a8\uc785\ub2c8\ub2e4.  \uc608\uc57d \uc804 \uc5f0\ub77d \ubc14\ub78d\ub2c8\ub2e4.'), STRINGDECODE('\ub300\uba85\ud22c\uc5b4, \uc6d4\uc545\uc0b0\uc5d0 \uc548\uae30\ub2e4.'));
